import fs from "fs/promises";
import satori from "satori";
import sharp from "sharp";
import { html } from "satori-html";
import type { APIContext } from "astro";

export async function getStaticPaths() {
  return [
    { params: { title: "404" }, props: { description: "Page not found" } },
    {
      params: { title: "schnelltest" },
      props: {
        description:
          "A website dedicated to providing information about various COVID-19 testing centers",
      },
    },
    {
      params: { title: "hm-medizintechnik" },
      props: {
        description:
          "Rental website for HM Medizintechnik, offering a wide range of medical devices",
      },
    },
    {
      params: { title: "hankens-testzentrum" },
      props: {
        description:
          "A platform enabling users to easily locate nearby COVID-19 testing centers.",
      },
    },
    {
      params: { title: "index" },
      props: {
        description: "Hey, I'm Anton, a developer, designer, and musician",
      },
    },
    {
      params: { title: "legal-notice" },
      props: { description: "Legal notice and disclaimer" },
    },
  ];
}

export async function GET({ props }: APIContext) {
  const { description } = props;
  const font = await fs.readFile("src/styles/fonts/Satoshi-Light.ttf");

  const template = html(`
    <div class="relative flex h-full flex-col bg-neutral-50">
      <div class="absolute flex -top-16 right-16">
        <img 
          src="data:image/png;base64," 
          height="500" 
          width="500" 
        />
      </div>
      <div class="flex absolute bottom-16 left-16 max-w-[2000px] font-satoshi-light text-[9rem] leading-none">
        ${description}
      </div>
    </div>
  `);

  const svg = await satori(template, {
    width: 2400,
    height: 1260,
    fonts: [{ name: "Satoshi", data: font }],
  });

  const png = await sharp(Buffer.from(svg)).png().toBuffer();

  return new Response(png, {
    headers: {
      "Content-Type": "image/png",
    },
  });
}
